let posts;document.body.onload=async e=>{console.log("document loaded!"),posts=await fetch("https://www.techiediaries.com/api/feed.json").then((function(e){return e.json()})),posts.forEach((e=>{const a=document.createElement("div");a.classList.add("mt-1"),["card"].forEach((e=>{a.classList.add(e)})),a.innerHTML=`\n                <hr>\n                <div class="card-body">\n                    <h2 class="card-title">${e.title}</h2>\n                    <p class="card-text">${e.excerpt}</p>\n                    <div class="card-footer">\n                        <a href="${e.url}" class="card-link">Read</a>\n                        <a href="#" class="card-link">Save to read offline</a>\n                    </div>\n\n                </div>`,document.getElementById("postsDiv").appendChild(a)}))};